mapper.select:
select  from movies_address_t m where m.is_deleted = 0 

  and (
		m.name like concat("%", #{query}, "%")
	 )


	limit #{start}, #{limit}

mapper.update:
update movies_address_t set 
		name = #{name},
		address = #{address}
where
		id = #{id}

mapper.select:
select from film_movies_t fm left join film_t f on f.id = fm.film_id left join movies_address_t ma on ma.id = fm.movies_id where ma.is_deleted = 0 and f.is_deleted = 0 and fm.is_deleted = 0
and fm.movies_id = #{moviesId}
and fm.film_id = #{filmId}
